var app=angular.module("waitstaff",[]);app.controller("calculator",["$scope",function(a){a.resetVariables=function(){a.mealPrice=0,a.taxRate=0,a.tipPercent=0},a.initCharges=function(){a.subtotal=0,a.tip=0,a.total=0},a.initEarnings=function(){a.earningsTipTotal=0,a.mealCount=0,a.earningsAvgTip=0},a.calculateMealValues=function(){a.subtotal=a.mealPrice+a.mealPrice*(a.taxRate/100),a.tip=(a.mealPrice+a.mealPrice*(a.taxRate/100))*(a.tipPercent/100),a.total=a.subtotal+a.tip},a.init=function(){a.resetVariables(),a.initCharges(),a.initEarnings(),a.calculateMealValues()},a.init()}]);