var app=angular.module("waitstaff",[]);app.controller("calculator",["$scope",function(t){t.resetVariables=function(){t.mealPrice=0,t.taxRate=0,t.tipPercent=0},t.initCharges=function(){t.subtotal=0,t.tip=0,t.total=0},t.initEarnings=function(){t.earningsTipTotal=0,t.mealCount=0,t.earningsAvgTip=0},t.init=function(){t.resetVariables(),t.initCharges(),t.initEarnings()},t.init(),t.submitMealDetails=function(){t.enterMealDetails.$invalid?t.formError="Please enter valid meal details.":(t.formError="",t.earningsTipTotal+=t.tip,t.mealCount++)},t.$watchGroup(["mealPrice","taxRate","tipPercent"],function(a,i,e){t.enterMealDetails.$invalid?t.initCharges():(t.formError="",t.subtotal=t.mealPrice*(1+t.taxRate/100),t.tip=t.mealPrice*(t.tipPercent/100),t.total=t.subtotal+t.tip)}),t.$watchGroup(["tipTotal","mealCount"],function(a,i,e){0!==t.mealCount?t.earningsAvgTip=t.earningsTipTotal/t.mealCount:t.earningsAvgTip=0})}]);